using Microsoft.AspNetCore.Mvc;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using Shooping.Models;

namespace Shooping.Controllers
{
    public class Products : Controller
    {
        List<ProductModel> productsList = new List<ProductModel>() {
                new ProductModel {ID=1, Name="iPhone", Price=2393, Image ="https://media.extra.com/s/aurora/100199389_800/Apple-iPhone-12-MINI-128GB-Blue?locale=en-GB,en-" },
                new ProductModel {ID=2, Name="HardDisk", Price=9399, Image = "data:image/jpeg;base64," },
                new ProductModel {ID=3, Name="Mouse", Price=120, Image = "https://thermaltake.azureedge.net/pub/media/catalog/product/cache/e4fc6e308b66431a310dcd4dc0838059/l/2/l20m01.jpg" }

            };

        public IActionResult Index(string color)
        {
            ViewData["colord"] = color;
            ViewBag.ProductModel = "ProductModel List";
            return View(productsList);
        }

        public IActionResult Details(int id)
        {
            ProductModel Product = productsList.Find(P => P.ID == id);
            if (Product == null)
            {
                return Content("No book in this name");
            }
            else
            {
                ViewData["Products"] = Product;
                return View();
            }
        }

        public IActionResult Random()
        {
            return View();
        }
    }
}
